import React ,{createContext,useState}from 'react'
import Section1 from "./Section1.js"
import Section2 from "./Section2.js"
import FooterPage from "./Footer.js"


export const cartState=createContext(0)
function Home() {
    const cartCounter=useState(0)
    console.log(cartCounter,"usestate")

    return (
        <div>
            <cartState.Provider  value={   cartCounter   }>
             <Section1/>
             <Section2 />

             < FooterPage />
             </cartState.Provider>

        </div>
    )
}

export default Home
